import React, { useEffect, useState } from "react";
import axios from "axios";
import { Container, Row, Col, Card, Form, Button, Alert } from "react-bootstrap";

// Mapeo número → nombre
const PREF_MAP = {
  0: "water",
  1: "beer",
  2: "juice",
  3: "soda",
  4: "milk",
  5: "coffee",
  6: "tea",
  7: "peach_juice"
};

// Mapeo nombre → número para enviar al backend
const PREF_MAP_INVERSE = Object.entries(PREF_MAP).reduce((acc, [key, val]) => {
  acc[val] = Number(key);
  return acc;
}, {});

// Convierte el usuario recibido (array con números) a objeto con nombres en preferences
function transformUser(raw) {
  const name = raw[0];
  const email = raw[1];
  const affiliate = raw[raw.length - 1];
  const prefsNums = raw.slice(2, raw.length - 1);
  const preferences = prefsNums.map(i => PREF_MAP[i]).filter(Boolean);
  return { name, email, preferences, affiliate };
}

function App() {
  const [form, setForm] = useState({
    name: '',
    email: '',
    preferences: '', // string con nombres separados por comas
    affiliate: false
  });

  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Traer usuarios y transformar para mostrar nombres
  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:8000/api/get_users/");
      const transformed = res.data.map(u => transformUser(u));
      setUsers(transformed);
    } catch (err) {
      console.error("Error al obtener usuarios:", err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validar y convertir nombres a índices
    let prefsArray = [];
    if (typeof form.preferences === 'string') {
      prefsArray = form.preferences
        .split(',')
        .map(p => p.trim().toLowerCase())
        .filter(p => p.length > 0)
        .map(name => PREF_MAP_INVERSE[name])
        .filter(i => i !== undefined);
    } else if (Array.isArray(form.preferences)) {
      prefsArray = form.preferences;
    }

    if (prefsArray.length === 0) {
      setError('Debe introducir al menos una preferencia válida.');
      return;
    }

    try {
      await axios.post("http://localhost:8000/api/post_user/", {
        name: form.name,
        email: form.email,
        preferences: prefsArray,
        affiliate: form.affiliate
      });
      setForm({ name: '', email: '', preferences: '', affiliate: false });
      fetchUsers();
      setSuccess('Usuario añadido correctamente!');
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      setError(err.response?.data?.error || 'Error desconocido');
    }
  };

  return (
    <Container fluid className="p-4">
      <Row>
        <Col md={4}>
          <h3>Registrar Usuario</h3>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-2">
              <Form.Label>Nombre</Form.Label>
              <Form.Control
                value={form.name}
                onChange={e => setForm({ ...form, name: e.target.value })}
                required
              />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                value={form.email}
                onChange={e => setForm({ ...form, email: e.target.value })}
                required
              />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Preferencias (nombres separados por comas)</Form.Label>
              <Form.Control
                value={form.preferences}
                onChange={e => setForm({ ...form, preferences: e.target.value })}
                placeholder="Ej: water, soda, peach_juice"
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Check
                type="checkbox"
                label="Afiliado"
                checked={form.affiliate}
                onChange={e => setForm({ ...form, affiliate: e.target.checked })}
              />
            </Form.Group>

            <Button type="submit">Enviar</Button>
          </Form>

          {error && <Alert variant="danger" className="mt-3">{error}</Alert>}
          {success && <Alert variant="success" className="mt-3">{success}</Alert>}
        </Col>

        <Col md={8}>
          <h3>Usuarios</h3>
          <Row>
            {users.map((u, idx) => (
              <Col sm={6} lg={4} key={idx} className="mb-3">
                <Card
                  style={{
                    backgroundColor: u.affiliate ? "#d4edda" : "#e2e3e5",
                    borderRadius: "12px",
                    boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                    transition: "transform 0.2s",
                    cursor: "pointer"
                  }}
                  onMouseEnter={e => (e.currentTarget.style.transform = "scale(1.05)")}
                  onMouseLeave={e => (e.currentTarget.style.transform = "scale(1)")}
                >
                  <Card.Body>
                    <Card.Title>{u.name}</Card.Title>
                    <Card.Text>
                      <a href={`mailto:${u.email}`}>{u.email}</a><br />
                      <strong>Preferencias:</strong>
                      <ul className="mb-0">
                        {u.preferences.sort().map((p, i) => (
                          <li key={i}>
                            <img
                              src={`https://via.placeholder.com/20?text=${p}`}
                              alt={p}
                              className="me-1"
                            />
                            {p}
                          </li>
                        ))}
                      </ul>
                    </Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
